import React, { useState } from 'react';

// Sample data structure for articles and topics (you'll replace this with actual data)
const topicDisplayNames = {
  Halsa: 'Hälsa',
  SamhalleKonflikter: 'Samhälle och Konflikter',
  Miljo: 'Miljö',
  VetenskapTeknik: 'Vetenskap och Teknik',
  LivsstillFritt: 'Livsstil och Fritid',
  Ekonomi: 'Ekonomi',
  Religion: 'Religion',
  Idrott: 'Idrott',
};

const ArticleList = ({ articles }) => {
  const [selectedTopic, setSelectedTopic] = useState('');
  const [sortBy, setSortBy] = useState('');

  const handleTopicChange = (e) => {
    setSelectedTopic(e.target.value);
  };

  const handleSortByChange = (e) => {
    setSortBy(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to filter/sort articles based on selectedTopic and sortBy
  };

  return (
    <div className="container">
      <h1>Articles</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="topic">Select a topic:</label>
        <select id="topic" name="topic" className="form-select" value={selectedTopic} onChange={handleTopicChange}>
          <option value="">All</option>
          {Object.keys(topicDisplayNames).map((key) => (
            <option key={key} value={key}>{topicDisplayNames[key]}</option>
          ))}
        </select>

        <label htmlFor="sortBy">Sort by:</label>
        <select id="sortBy" name="sortBy" className="form-select" value={sortBy} onChange={handleSortByChange}>
          <option value="">Default</option>
          <option value="newest">Newest to Oldest</option>
          <option value="oldest">Oldest to Newest</option>
        </select>

        <button type="submit" className="btn btn-primary">Filter</button>
      </form>

      <div className="row mt-4" id="articlesContainer">
        {articles.map((article) => (
          <div key={article.id} className="col-md-6 col-lg-4 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{article.title}</h5>
                <p className="card-text">{article.summary}</p>
                <a href={article.link} className="card-link" target="_blank" rel="noopener noreferrer">Read More</a>
                <p className="card-text"><small className="text-muted">{new Date(article.published).toLocaleString()}</small></p>
              </div>
            </div>
          </div>
        ))}
      </div>
      <button className="scroll-to-top" onClick={() => window.scrollTo(0, 0)}>Go to top</button>
    </div>
  );
};

export default ArticleList;
